import {
  Button,
  ButtonProps,
  Modal,
  ModalSize,
  useModal,
} from '@openfun/cunningham-react';
import React from 'react';
import { useTranslation } from 'react-i18next';

import { Box, Text } from '@/components';

export const Feedback = (_props: { buttonProps?: Partial<ButtonProps> }) => {
  const { t } = useTranslation();
  const modal = useModal();

  return (
    <>
      <Button
        icon={<InfoIcon />}
        fullWidth
        onClick={() => modal.open()}
        className="c__button--info"
      >
        {t('Give feedback')}
      </Button>
      <Modal
        closeOnClickOutside
        {...modal}
        title={t('New feedback')}
        size={ModalSize.MEDIUM}
      >
        <Box>
          <Text
            $size="m"
            $margin={{ top: 'xs', bottom: 'base' }}
            style={{ lineHeight: '1.5' }}
          >
            {t(
              'Assistant is in development: your feedback matters! Choose how to share your ideas:',
            )}
          </Text>

          <div
            style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}
          >
            <FeedbackButton
              href="https://formulaire.beta.numerique.gouv.fr/r/assistant"
              icon={<FormIcon />}
              title={t('Give a quick opinion')}
              description={t(
                '30 sec to tell us what you think or report a bug',
              )}
            />

            <FeedbackButton
              href="https://www.tchap.gouv.fr/#/room/!gpLYRJyIwdkcHBGYeC:agent.dinum.tchap.gouv.fr"
              icon={<TchapIcon />}
              title={t('Write on Tchap')}
              description={t('Direct exchange with our team')}
            />

            {/*            <FeedbackButton
              href="https://app.calendso.incubateur.net/team/lasuite/retours-fichiers"
              icon={<VisioIcon />}
              title={t('Talk in video')}
              description={t('20 min for an in-depth discussion')}
            />*/}
          </div>
        </Box>
      </Modal>
    </>
  );
};

const FeedbackButton = ({
  href,
  icon,
  title,
  description,
}: {
  href: string;
  icon: React.ReactElement;
  title: string;
  description: string;
}) => {
  return (
    <Box
      as="a"
      $direction="row"
      href={href}
      target="_blank"
      rel="noopener noreferrer"
      $padding="20px"
      $radius="4px"
      $color="greyscale-600"
      $gap="20px"
      $css={`
        display: flex;
        text-decoration: none;
        align-items: center;
        transition: all 0.2s ease;
        cursor: pointer;
        border: 1px solid var(--c--theme--colors--greyscale-100);
        color: var(--c--theme--colors--greyscale-600) !important;
        
        &:hover {
          border-color: #b6d7fb;
          background-color: #e7f2fd;
          color: var(--c--theme--colors--info-600) !important;
          span {
            color: var(--c--theme--colors--info-600) !important;
          }
        }
      `}
    >
      <div style={{ flexShrink: 0 }}>{icon}</div>
      <Box $direction="column" $gap="xs">
        <Text $weight="700" $size="sm" $color="greyscale-900">
          {title}
        </Text>
        <Text $size="sm" $color="greyscale-600" style={{ lineHeight: '1.4' }}>
          {description}
        </Text>
      </Box>
    </Box>
  );
};

